INSERT INTO `travelviewing` (`id`, `title`, `iva1`, `iva2`, `iva3`, `content`, `code`, `Dday`, `time`, `startingplace`, `ToLocation`, `Numberofseats`) VALUES
(10, 'Ao Bà OM', './upload/image/ao-ba-om.jpg', './upload/image/aobaom-1.jpg', './upload/image/aobaom-6.jpg', '<h1><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">AO B&Agrave; OM &ndash; THẮNG CẢNH ĐỘC Đ&Aacute;O CỦA ĐẤT TR&Agrave; VINH</span></span></h1>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nhắc tới Tr&agrave; Vinh, người ta nghĩ đến mảnh đất của những ng&ocirc;i ch&ugrave;a Khmer cổ k&iacute;nh c&ugrave;ng những di t&iacute;ch lịch sử mang nhiều truyền thuyết huyền thoại, gắn liền với h&agrave;nh tr&igrave;nh khai ph&aacute;, gầy dựng phương Nam. Di t&iacute;ch ao B&agrave; Om ng&agrave;n năm soi b&oacute;ng cổ tự l&agrave; một trong những niềm tự h&agrave;o của người d&acirc;n nơi đ&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/ao-ba-om.jpg\" style=\"height:1066px; width:1600px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">To&agrave;n cảnh Ao B&agrave; Om từ tr&ecirc;n cao &ndash; Ảnh: Daihocsi</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ao B&agrave; Om, hay Ao Vu&ocirc;ng nằm cạnh Quốc lộ 53, thuộc phường 8 th&agrave;nh phố Tr&agrave; Vinh (trước đ&acirc;y l&agrave; ấp T&agrave; Cụ, x&atilde; Nguyệt H&oacute;a, huyện Ch&acirc;u Th&agrave;nh), c&aacute;ch trung t&acirc;m th&agrave;nh phố Tr&agrave; Vinh khoảng 5 km về ph&iacute;a T&acirc;y Nam.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-6.jpg\" style=\"height:1268px; width:1984px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ao B&agrave; Om hay c&ograve;n gọi Ao Vu&ocirc;ng</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a href=\"https://thamhiemmekong.com/thong-tin-du-lich-mien-tay/dia-diem-du-lich-tra-vinh-tuong-nhat-dinh-phai-di.html\" target=\"_top\" title=\"Du lịch Trà Vinh\">Du lịch Tr&agrave; Vinh</a>&nbsp;đến đ&acirc;y, bạn kh&ocirc;ng đơn thuần chỉ được chi&ecirc;m ngưỡng vẻ đẹp thơ mộng của thi&ecirc;n nhi&ecirc;n m&agrave; c&ograve;n được cảm nhận r&otilde; n&eacute;t hơn những gi&aacute; trị văn ho&aacute; độc đ&aacute;o của v&ugrave;ng đất Tr&agrave; Vinh qua c&acirc;u chuyện mang đậm m&agrave;u sắc Khmer Nam Bộ&hellip;</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-5.jpg\" style=\"height:1229px; width:2034px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ao c&oacute; h&igrave;nh chữ nhật, rộng 300m, d&agrave;i 500m</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ấn tượng đầu ti&ecirc;n khi tới thăm ao B&agrave; Om l&agrave; cảm gi&aacute;c m&aacute;t mẻ trước cảnh trời nước xanh biếc một m&agrave;u. Ao c&oacute; h&igrave;nh chữ nhật, rộng 300m, d&agrave;i 500m (v&igrave; gần với h&igrave;nh vu&ocirc;ng n&ecirc;n c&ograve;n được gọi l&agrave; Ao Vu&ocirc;ng) kh&aacute;ch sẽ bất ngờ v&igrave; ao lớn qu&aacute;, phải gọi l&agrave; hồ th&igrave; đ&uacute;ng hơn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-4.jpg\" style=\"height:1244px; width:2034px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Bao bọc xung quanh tr&ecirc;n bờ ao l&agrave; rất nhiều c&acirc;y cổ thụ</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Bao bọc xung quanh tr&ecirc;n bờ ao l&agrave; rừng c&acirc;y cổ thụ, đa số l&agrave; c&acirc;y sao, c&acirc;y dầu h&agrave;ng trăm năm tuổi, c&oacute; rễ x&ugrave; x&igrave; trồi l&ecirc;n mặt đất cả m&eacute;t th&agrave;nh những h&igrave;nh th&ugrave; lạ mắt bốn m&ugrave;a rợp b&oacute;ng th&acirc;m u tạo kh&ocirc;ng gian thanh b&igrave;nh, y&ecirc;n tĩnh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-3.jpg\" style=\"height:1272px; width:1994px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&ocirc;ng kh&iacute; trong l&agrave;nh tho&aacute;ng m&aacute;t</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&oacute; bộ rễ lớn đến nỗi c&oacute; thể tạo th&agrave;nh c&aacute;i hang độc nhất v&ocirc; nhị, trẻ con c&oacute; thể chui v&agrave;o vui chơi. Lại c&oacute; bộ rễ c&acirc;y trở th&agrave;nh ghế ngồi nghỉ ch&acirc;n của kh&aacute;ch. Người bản xứ l&yacute; giải rằng, những bộ rễ đồ sộ, quấn lấy nhau v&agrave; nằm tr&ecirc;n cao c&aacute;ch mặt đất như ng&agrave;y nay l&agrave; do khu đất xung quanh ao bị sụt l&uacute;n xuống thấp, rễ c&acirc;y lộ thi&ecirc;n v&agrave; ph&aacute;t triển theo thời gian.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-03.jpg\" style=\"height:1571px; width:2048px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Bộ rễ c&acirc;y c&oacute; nhiều h&igrave;nh th&ugrave; kỳ dị</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">K&iacute;ch cỡ khổng lồ v&agrave; h&igrave;nh th&ugrave; kỳ dị của c&aacute;c bộ rễ c&acirc;y ở ao B&agrave; Om khiến nhiều người li&ecirc;n tưởng đến một c&aacute;nh rừng cổ t&iacute;ch.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-2.jpg\" style=\"height:1534px; width:1506px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-12.jpg\" style=\"height:1360px; width:2048px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nếu đến đ&uacute;ng m&ugrave;a th&igrave; ao c&ograve;n được t&ocirc; điểm bởi những b&ocirc;ng sen hồng, b&ocirc;ng s&uacute;ng l&atilde;ng mạn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-02.jpg\" style=\"height:1072px; width:1600px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Những b&ocirc;ng sen hồng, b&ocirc;ng s&uacute;ng l&atilde;ng mạn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ngo&agrave;i cảnh đẹp đến m&ecirc; mẩn l&ograve;ng người, ao nước rộng lớn n&agrave;y c&ograve;n lung linh huyền ảo bởi những c&acirc;u chuyện nửa hư nửa thực từ bao đời nay ăn s&acirc;u v&agrave;o tiềm thức người d&acirc;n địa phương.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo truyền thuyết ng&agrave;y trước, v&ugrave;ng đất Tr&agrave; Vinh hằng năm cứ đến m&ugrave;a hạn th&igrave; nước ngọt khan hiếm, ruộng rẫy kh&ocirc; cằn, c&acirc;y cỏ chết h&eacute;o, người d&acirc;n trong v&ugrave;ng v&igrave; hạn h&aacute;n rơi v&agrave;o cảnh lầm than. Để cứu d&acirc;n khỏi cảnh khốn c&ugrave;ng, một &ocirc;ng ho&agrave;ng trấn nhậm trong v&ugrave;ng quy tụ b&agrave; con đ&agrave;o ao t&igrave;m nguồn nước.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">T&igrave;nh cờ, trong v&ugrave;ng l&uacute;c đ&oacute; cũng xảy ra một vụ tranh c&atilde;i kh&oacute; ph&acirc;n xử l&agrave; đ&agrave;n &ocirc;ng v&agrave; đ&agrave;n b&agrave;, ai phải đi cưới ai v&agrave; ai phải chịu mọi ph&iacute; tổn trong lễ cưới? &Ocirc;ng ho&agrave;ng nh&acirc;n dịp n&agrave;y chia ra hai b&ecirc;n nam nữ tổ chức một cuộc thi đ&agrave;o ao. Ao b&ecirc;n n&agrave;o đ&agrave;o s&acirc;u hơn, lớn hơn v&agrave; xong trước th&igrave; thắng, b&ecirc;n thua sẽ phải đi cưới.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-8.jpg\" style=\"height:1076px; width:1875px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ao B&agrave; Om gắn với nhiều truyền thuyết huyền thoại của người Khmer Nam Bộ</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B&ecirc;n nam th&igrave; đ&agrave;o ao tr&ograve;n ở ph&iacute;a T&acirc;y c&ograve;n b&ecirc;n nữ đ&agrave;o ao vu&ocirc;ng ở ph&iacute;a Đ&ocirc;ng. B&ecirc;n nữ do b&agrave; Om, một phụ nữ Khmer chỉ huy, thấy kh&ocirc;ng thể k&igrave;nh được sức đ&agrave;n &ocirc;ng n&ecirc;n b&ecirc;n nữ d&ugrave;ng &ldquo;kế&rdquo;: Họ vừa đ&agrave;o vừa ca m&uacute;a để c&aacute;c ch&agrave;ng bỏ việc m&agrave; chạy sang xem. Nửa đ&ecirc;m, b&agrave; Om cho chặt một c&acirc;y tre thật d&agrave;i, treo ngọn đ&egrave;n lồng rồi đem cắm ở hướng Đ&ocirc;ng. Theo giao hẹn l&agrave; khi sao Mai mọc l&agrave; phải ngừng c&ocirc;ng việc, khi b&ecirc;n nam thấy ngọn đ&egrave;n tưởng l&agrave; sao Mai n&ecirc;n họ rủ nhau về nghỉ. Trong l&uacute;c đ&oacute; b&ecirc;n nữ đ&agrave;o đến s&aacute;ng v&agrave; xong việc trước. B&ecirc;n nam thua cuộc trong sự &ldquo;t&acirc;m phục, khẩu phục&rdquo;. Để nhớ ơn người phụ nữ mưu tr&iacute;, người ta lấy t&ecirc;n b&agrave; đặt t&ecirc;n ao, từ đ&oacute; ao phụ nữ đ&agrave;o được gọi l&agrave; ao B&agrave; Om. V&agrave; truyền thống nam đi cưới nữ, con phải lấy họ mẹ trong d&acirc;n tộc Khmer cũng bắt đầu từ đ&acirc;y. M&atilde;i đến sau n&agrave;y khi người Ph&aacute;p cai trị nước ta th&igrave; con mới lấy theo họ cha.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-10.jpg\" style=\"height:1270px; width:1998px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Truyền thống con phải lấy họ mẹ trong d&acirc;n tộc Khmer bắt đầu từ đ&acirc;y</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cũng giải th&iacute;ch cho t&ecirc;n gọi ao B&agrave; Om&nbsp; c&ograve;n&nbsp; c&oacute; một&nbsp; c&acirc;u&nbsp; chuyện kh&aacute;c: Xưa kia c&oacute; một vị ho&agrave;ng tử rất độc &aacute;c trấn nhậm v&ugrave;ng đất Tr&agrave; Vinh, bắt d&acirc;n&nbsp; ch&uacute;ng phải d&acirc;ng g&aacute;i đẹp cho &ocirc;ng&nbsp; ta, ai bất tu&acirc;n sẽ bị trừng trị nặng. Vị&nbsp; ho&agrave;ng tử n&agrave;y buộc phụ nữ phải đem lễ vật đi cưới đ&agrave;n &ocirc;ng. Một&nbsp; h&ocirc;m,&nbsp; c&oacute; một&nbsp; c&ocirc;&nbsp; g&aacute;i&nbsp; xinh&nbsp; đẹp đến gặp ho&agrave;ng tử để b&agrave;y tỏ sự phản đối về tập tục bất hợp l&yacute; n&agrave;y. V&igrave; bị l&ocirc;i cuốn bởi vẻ đẹp của c&ocirc; g&aacute;i n&ecirc;n ho&agrave;ng tử muốn l&agrave;m vừa l&ograve;ng người đẹp, vừa muốn x&oacute;a bỏ tập&nbsp; tục m&igrave;nh đặt&nbsp; ra bằng c&aacute;ch cho mở một cuộc thi đ&agrave;o ao. Sau đ&oacute; mọi chuyện diễn ra như chuyện kể tr&ecirc;n.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo c&aacute;c nh&agrave; sử học v&agrave; nghi&ecirc;n cứu văn h&oacute;a d&acirc;n gian th&igrave; c&oacute; khoảng 10 dị bản để giải th&iacute;ch địa danh ao B&agrave; Om&nbsp; gồm đủ c&aacute;c thể loại của truyện kể d&acirc;n gian như: truyện cổ t&iacute;ch, truyện d&atilde; sử, truyền thuyết, giai&nbsp; thoại&hellip; C&oacute; thể n&oacute;i đ&acirc;y l&agrave; một trường hợp c&oacute; nhiều giả&nbsp; thuyết nhất về t&ecirc;n gọi địa&nbsp; danh ở Đồng bằng s&ocirc;ng Cửu Long. X&eacute;t về mặt nội dung hầu hết c&aacute;c chuyện xung quanh 3 chủ đề ch&iacute;nh: Giải th&iacute;ch t&ecirc;n gọi Ao B&agrave; Om, l&yacute;&nbsp; giải&nbsp; người&nbsp; nam đi cưới người&nbsp; nữ v&agrave; tại sao người Khmer c&oacute; tục lệ theo họ mẹ? C&aacute;c chuyện kể đều l&agrave; sản phẩm của tr&iacute; tưởng tượng mang đậm dấu ấn văn h&oacute;a của người Khmer ở Nam Bộ.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khi nắng chiều bu&ocirc;ng xuống tr&ecirc;n những h&agrave;ng c&acirc;y cổ thụ cao v&uacute;t, th&acirc;m u, d&agrave;i xa t&iacute;t tắp th&igrave; Ao B&agrave; Om ch&iacute;nh l&agrave; nơi dạo chơi l&yacute; tưởng nhất ở th&agrave;nh phố Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-1.jpg\" style=\"height:1014px; width:2048px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Điểm đi dạo l&yacute; tưởng</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hai b&ecirc;n con đường ch&iacute;nh dẫn v&agrave;o Ao B&agrave; Om l&agrave; một khu &ldquo;chợ trời&rdquo; ẩm thực rất nhộn nhịp, đ&ocirc;ng vui. Du kh&aacute;ch c&oacute; thể tản bộ ngắm cảnh v&agrave; khi n&agrave;o cảm thấy &ldquo;x&oacute;t ruột&rdquo; th&igrave; gh&eacute; v&agrave;o một g&aacute;nh h&agrave;ng rong, hoặc một qu&aacute;n ăn nhỏ gọn để thưởng thức những m&oacute;n ẩm thực nổi tiếng của Tr&agrave; Vinh như đ&aacute; b&agrave;o sữa, b&uacute;n mắm, b&aacute;nh canh, b&uacute;n c&agrave; ri, b&aacute;nh thốt nốt&hellip; m&oacute;n ăn n&agrave;o gi&aacute; cả cũng rất mềm.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-11.jpg\" style=\"height:1365px; width:2048px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;V&agrave;o những ng&agrave;y lễ, tết h&agrave;ng năm của người Khmer, ao B&agrave; Om trở th&agrave;nh nơi sinh hoạt cộng đồng n&aacute;o nhiệt của cả v&ugrave;ng, nhất l&agrave; v&agrave;o Lễ hội Ok Om Bok được tổ chức v&agrave;o rằm th&aacute;ng 10 &Acirc;m lịch, thu h&uacute;t h&agrave;ng ng&agrave;n người d&acirc;n khắp nơi về đ&acirc;y tham dự. Họ c&ugrave;ng nhau nhảy m&uacute;a, xem h&aacute;t D&ugrave; k&ecirc;, &hellip;thắt chặt th&ecirc;m t&igrave;nh đo&agrave;n kết, h&ograve;a hợp c&aacute;c d&acirc;n tộc anh em ở v&ugrave;ng s&ocirc;ng nước Cửu Long. V&agrave; khi m&agrave;n đ&ecirc;m bu&ocirc;ng xuống, khu vực Ao B&agrave; Om lung linh, huyền ảo, n&aacute;o nhiệt với Hội thả đ&egrave;n gi&oacute;, c&oacute; rất nhiều loại đ&egrave;n đủ k&iacute;ch cỡ được thả bay l&ecirc;n trời mang theo lời khấn nguyện, ước mong trời đất giao h&ograve;a, m&ugrave;a m&agrave;ng tốt tươi, con người v&agrave;&nbsp;vạn vật b&igrave;nh y&ecirc;n, dồi d&agrave;o sức khỏe.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom-7.jpg\" style=\"height:1365px; width:2048px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thả hoa đăng tr&ecirc;n Ao B&agrave; Om</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B&ecirc;n cạnh ao l&agrave; ng&ocirc;i ch&ugrave;a &Acirc;ng, một trong những ng&ocirc;i ch&ugrave;a Khmer cổ nhất Tr&agrave; Vinh được x&acirc;y dựng từ năm 990, v&agrave; Bảo t&agrave;ng văn ho&aacute; Khmer cũng được đặt ở đ&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/aobaom.jpg\" style=\"height:1365px; width:2048px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B&ecirc;n cạnh ao l&agrave; ng&ocirc;i ch&ugrave;a &Acirc;ng</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ao B&agrave; Om được Bộ Văn h&oacute;a Th&ocirc;ng tin c&ocirc;ng nhận l&agrave; di t&iacute;ch lịch sử văn h&oacute;a cấp quốc gia v&agrave;o năm 1994. C&ugrave;ng với bảo t&agrave;ng văn h&oacute;a v&agrave; ch&ugrave;a &Acirc;ng, bộ 3 quần thể danh thắng n&agrave;y l&agrave; một trong những điểm kh&ocirc;ng thể bỏ lỡ khi tới thăm Tr&agrave; Vinh. Nếu đang ph&acirc;n v&acirc;n t&igrave;m một điểm đến cho k&igrave; nghỉ sắp tới, th&igrave; Tr&agrave; Vinh với Ao B&agrave; Om ch&iacute;nh l&agrave; một điểm đến l&yacute; tưởng cho&nbsp;</span></span><iframe frameborder=\"0\" height=\"411\" id=\"442318703\" src=\"https://www.youtube.com/embed/kOVxICZjsCk?enablejsapi=1&amp;origin=https%3A%2F%2Fwww.dienmayxanh.com\" title=\"Ao Bà Om ở Trà Vinh\" width=\"730\"></iframe></p>\r\n', 'Ao Bà Om nằm cách trung tâm thành phố Trà Vinh khoảng 7 km dọc theo Quốc lộ 53 về phía Tây Nam.', '2023-12-11', 0, 'Ao Bà Om có các cây cổ thụ bao quanh và những cây dầu thụ trăm năm, mặt ao được phủ bởi hoa sen tạo nên một màu phong phú.', 'Hình dạng vuông độc đáo với chiều rộng khoảng 300m, chiều dài khoảng 500m tạo nên một sự khác biệt.', 10),
(11, 'Biển Ba Động', './upload/image/bien-ba-dong-tra-vinh.jpg', './upload/image/bai-bien-ba-dong.jpg', './upload/image/gt12.jpg', '<h1><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;m ph&aacute; Khu du lịch Biển Ba Động &ndash; Tr&agrave; Vinh</span></span></h1>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Khám phá Khu du lịch Biển Ba Động - Trà Vinh 2022\" src=\"https://dulichtoivaban.com/wp-content/uploads/2022/08/Kham-pha-Khu-du-lich-Bien-Ba-Dong-Tra-Vinh-2022-930x620.jpg\" style=\"height:710px; width:1065px\" title=\"Khám phá Khu du lịch Biển Ba Động - Trà Vinh 1\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Biển Ba Động</strong>&nbsp;l&agrave; một trong những điểm du lịch nổi tiếng bậc nhất ở Tr&agrave; Vinh, b&atilde;i biển c&oacute; vẻ đẹp hoang sơ trải d&agrave;i gần chục c&acirc;y số với kh&ocirc;ng kh&iacute; trong l&agrave;nh v&agrave; l&agrave;n nước biển đặc trưng b&ecirc;n những chong ch&oacute;ng điện gi&oacute; xoay v&ograve;ng như ch&agrave;o mời du kh&aacute;ch gần xa đến tham quan v&agrave; thưởng l&atilde;m.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Biển Ba Động Trà Vinh nhìn từ trên cao\" src=\"https://dulichtoivaban.com/wp-content/uploads/2022/08/Bien-Ba-Dong-Tra-Vinh-nhin-tu-tren-cao.jpg\" style=\"height:710px; width:1183px\" title=\"Khám phá Khu du lịch Biển Ba Động - Trà Vinh 2\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Biển Ba Động Tr&agrave; Vinh nh&igrave;n từ tr&ecirc;n cao.</span></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Biển Ba Động ở đ&acirc;u?</span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><a href=\"https://dulichtoivaban.com/du-lich-bien-ba-dong-tra-vinh.html\">Biển Ba Động</a></strong>&nbsp;l&agrave; danh thắng v&agrave; l&agrave; khu du lịch nổi tiếng thuộc x&atilde; Trường Long H&ograve;a (thị x&atilde; Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh), c&aacute;ch trung t&acirc;m th&agrave;nh phố Tr&agrave; Vinh 50 km về hướng đ&ocirc;ng nam v&agrave; c&aacute;ch thị x&atilde; Duy&ecirc;n Hải 10 km về hướng đ&ocirc;ng.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khu du lịch n&agrave;y c&oacute; vị tr&iacute; nằm giữa hai cửa biển Cung Hầu (s&ocirc;ng Tiền), Định An (s&ocirc;ng Hậu), nh&igrave;n ch&iacute;nh diện ra biển Đ&ocirc;ng. C&aacute;c bạn c&oacute; thể theo hướng dẫn chỉ đường sau để đi đến được khu du lịch Biển Ba Động Tr&agrave; Vinh nh&eacute;! [<strong><a href=\"https://goo.gl/maps/jfuwDXxrzRyrxXVCA\" rel=\"nofollow noopener\" target=\"_blank\">XEM BẢN ĐỒ</a></strong>]</span></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Lịch sử h&igrave;nh th&agrave;nh v&agrave; điểm đặc trưng của Biển Ba Động</span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">V&ugrave;ng biển Đ&ocirc;ng ven bờ c&aacute;c tỉnh Đồng bằng s&ocirc;ng Cửu Long l&agrave; v&ugrave;ng biển ph&ugrave; sa do nhiều cửa s&ocirc;ng lớn nhỏ đổ ra n&ecirc;n phần lớn l&agrave; b&atilde;i b&ugrave;n, nước kh&ocirc;ng trong. Trong đ&oacute;, biển Ba Động l&agrave; khu vực hiếm hoi ở miền T&acirc;y Nam bộ được thi&ecirc;n nhi&ecirc;n h&agrave;o ph&oacute;ng ban tặng cho một b&atilde;i c&aacute;t d&agrave;i hơn 10 km từ ấp Nh&agrave; M&aacute;t tới ấp Cồn Trứng.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Bãi biển Ba Động có độ dốc thoai thoải, khi nước thủy triều xuống, bãi cát phơi ra hàng trăm mét, từ bờ xuống tới mép nước.\" src=\"https://dulichtoivaban.com/wp-content/uploads/2022/08/Bai-bien-Ba-Dong-co-do-doc-thoai-thoai-khi-nuoc-thuy-trieu-xuong-bai-cat-phoi-ra-hang-tram-met-tu-bo-xuong-toi-mep-nuoc..jpg\" style=\"height:710px; width:950px\" title=\"Khám phá Khu du lịch Biển Ba Động - Trà Vinh 3\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B&atilde;i biển Ba Động c&oacute; độ dốc thoai thoải, khi nước thủy triều xuống, b&atilde;i c&aacute;t phơi ra h&agrave;ng trăm m&eacute;t, từ bờ xuống tới m&eacute;p nước.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cũng do nằm trong khu vực biển ph&ugrave; sa n&ecirc;n b&atilde;i c&aacute;t Ba Động kh&ocirc;ng trắng muốt hay v&agrave;ng &oacute;ng ả, nước biển Ba Động cũng kh&ocirc;ng thể trong xanh như với c&aacute;c b&atilde;i biển Nha Trang hay Vũng T&agrave;u. Tuy nhi&ecirc;n, dọc bờ biển Đ&ocirc;ng, từ G&ograve; C&ocirc;ng tới C&agrave; Mau, Ba Động c&oacute; b&atilde;i c&aacute;t đẹp, nước biển kh&aacute; trong, nhất l&agrave; v&agrave;o những th&aacute;ng sau Tết Nguy&ecirc;n đ&aacute;n, s&oacute;ng y&ecirc;n biển lặng, h&igrave;nh th&agrave;nh khu du lịch biển được nhiều người ưa chuộng.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Sớm nhận ra gi&aacute; trị của b&atilde;i biển Ba Động, từ đầu thế kỷ XX, nh&agrave; cầm quyền thực d&acirc;n Ph&aacute;p đ&atilde; x&acirc;y dựng ở đ&acirc;y khu nghỉ m&aacute;t v&agrave; gần đ&oacute; l&agrave; một s&acirc;n golf mini (l&uacute;c đ&oacute; s&acirc;n golf gọi l&agrave; s&acirc;n c&ugrave;, đ&aacute;nh golf gọi l&agrave; đ&aacute;nh c&ugrave;) d&agrave;nh cho c&aacute;c quan chức, giới thượng lưu trong tỉnh v&agrave; c&aacute;c tỉnh lận cận về nghỉ dưỡng dịp cuối tuần.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Qua giai đoạn chiến tranh &aacute;c liệt, khu nghỉ m&aacute;t v&agrave; s&acirc;n golf ấy kh&ocirc;ng c&ograve;n nhưng đ&atilde; để lại địa danh ấp Nh&agrave; M&aacute;t (thuộc x&atilde; Trường Long H&ograve;a, thị x&atilde; Duy&ecirc;n Hải) v&agrave; ấp Cồn C&ugrave; (thuộc x&atilde; Đ&ocirc;ng Hải, huyện Duy&ecirc;n Hải). Sau khi tỉnh Tr&agrave; Vinh được t&aacute;i lập (1992), Nh&agrave; nước tập trung nguồn kinh ph&iacute; đầu tư hệ thống kết cấu hạ tầng như giao th&ocirc;ng, điện lưới quốc gia, viễn th&ocirc;ng, rừng ph&ograve;ng hộ phi lao, bờ k&egrave; chống sạt lở&hellip; tạo điều kiện đ&aacute;nh thức tiềm năng du lịch biển Ba Động.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Dừng ch&acirc;n tại khu du lịch Biển Ba Động, du kh&aacute;ch sẽ c&oacute; dịp t&igrave;m hiểu bề d&agrave;y lịch sử, chiều s&acirc;u văn h&oacute;a của v&ugrave;ng đất ven biển&nbsp;<em>Trường Long H&ograve;a, Duy&ecirc;n Hải, Tr&agrave; Vinh</em>. Gần 3 thế kỷ trước, những ngư d&acirc;n v&ugrave;ng B&igrave;nh Thuận v&agrave;o định cư tại đ&acirc;y đ&atilde; mở ra nghề đi biển tạo cơm ăn &aacute;o mặc v&agrave; cũng ch&iacute;nh họ đ&atilde; mang theo địa danh&nbsp;<em>Ba Động</em>, t&iacute;n ngưỡng thờ&nbsp;<em>B&agrave; Cố Hỷ</em>&nbsp;cho v&ugrave;ng đất mới.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Di tích Lầu bà Cố Hỷ Thượng Động nương nương\" src=\"https://dulichtoivaban.com/wp-content/uploads/2022/08/Di-tich-Lau-ba-Co-Hy-Thuong-Dong-nuong-nuong.jpg\" style=\"height:710px; width:1068px\" title=\"Khám phá Khu du lịch Biển Ba Động - Trà Vinh 4\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Di t&iacute;ch Lầu b&agrave; Cố Hỷ Thượng Động nương nương.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Những năm th&aacute;ng qu&acirc;n tướng ch&uacute;a Nguyễn &Aacute;nh trốn chạy sự truy đuổi của nghĩa qu&acirc;n T&acirc;y Sơn v&agrave;o cuối thế kỷ XVIII đ&atilde; để lại c&aacute;c t&ecirc;n l&agrave;ng c&oacute; từ tố &ldquo;<strong>Long</strong>&rdquo; như&nbsp;<em>Trường Long H&ograve;a, Long Vĩnh, Long Hữu, Long To&agrave;n&hellip;</em>&nbsp;v&agrave; để lại lu&ocirc;n ng&ocirc;i mộ Quận ch&uacute;a như một b&iacute; ẩn lịch sử mấy trăm năm.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cuối thế kỷ XIX, khi thực d&acirc;n Ph&aacute;p nổ s&uacute;ng x&acirc;m chiếm Nam bộ, tuyến rừng ven biển Duy&ecirc;n Hải l&agrave; căn cứ của nghĩa binh&nbsp;<em>Đề Triệu, Phan T&ocirc;n &ndash; Phan Li&ecirc;m, L&ecirc; Tấn Kế &ndash; Trần B&igrave;nh</em>&hellip;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Trong hai cuộc kh&aacute;ng chiến chống thực d&acirc;n Ph&aacute;p v&agrave; đế quốc Mỹ trường kỳ của d&acirc;n tộc, Trường Long H&ograve;a n&oacute;i ri&ecirc;ng, Duy&ecirc;n Hải n&oacute;i chung l&agrave; căn cứ vững chắc của c&aacute;c cơ quan l&atilde;nh đạo, lực lượng vũ trang c&aacute;ch mạng tỉnh Tr&agrave; Vinh, Khu T&acirc;y Nam bộ v&agrave; cả Đặc khu S&agrave;i G&ograve;n &ndash; Gia Định.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ch&iacute;nh địa b&agrave;n n&agrave;y l&agrave; một trong những mắc x&iacute;ch quan trọng của tuyến đường Hồ Ch&iacute; Minh tr&ecirc;n biển huyền thoại vận chuyển vũ kh&iacute; từ miền Bắc x&atilde; hội chủ nghĩa v&agrave;o chi viện cho chiến trường T&acirc;y Nam bộ, với những k&igrave;nh ngư lẫy lừng t&ecirc;n tuổi l&agrave; những người con ưu t&uacute; của v&ugrave;ng đất n&agrave;y như&nbsp;<em>L&ecirc; Thanh L&ograve;ng, Hồ Đức Thắng</em>&hellip; Truyền thống vẻ vang đ&oacute; đ&atilde; tạo n&ecirc;n một &ldquo;<strong>Trường Long H&ograve;a sắt th&eacute;p</strong>&ldquo;, một &ldquo;<strong>Duy&ecirc;n Hải anh h&ugrave;ng</strong>&rdquo; m&atilde;i m&atilde;i l&agrave; niềm tự h&agrave;o của c&aacute;c thế hệ cư d&acirc;n địa phương.</span></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Biển Ba Động Tr&agrave; Vinh c&oacute; g&igrave;?</span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ng&agrave;y nay, một số doanh nghiệp trong, ngo&agrave;i tỉnh đ&atilde; v&agrave; đang đầu tư x&acirc;y dựng cơ sở vật chất phục vụ du lịch tiện &iacute;ch như kh&aacute;ch sạn, nh&agrave; h&agrave;ng, khu nghỉ dưỡng, bờ k&egrave; chống sạt lở, quy hoạch v&agrave; cắm phao an to&agrave;n b&atilde;i tắm, quầy vật phẩm lưu niệm&hellip; Từ đ&oacute;, kh&aacute;ch du lịch v&agrave; c&aacute;c đo&agrave;n tham quan từ c&aacute;c tỉnh miền T&acirc;y Nam bộ v&agrave; cả du kh&aacute;ch nước ngo&agrave;i đến khu du lịch Biển Ba Động ng&agrave;y c&agrave;ng đ&ocirc;ng hơn.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đến với khu du lịch Biển Ba Động, du kh&aacute;ch n&ecirc;n nghỉ lại qua đ&ecirc;m để c&oacute; điều kiện ngắm &aacute;nh b&igrave;nh minh hoặc &aacute;nh ho&agrave;ng h&ocirc;n tr&ecirc;n biển. Sau đ&oacute;, h&ograve;a v&agrave;o đo&agrave;n người đang thỏa th&iacute;ch vẫy v&ugrave;ng trong l&agrave;n nước biển m&aacute;t lạnh hay cưỡi m&ocirc; t&ocirc; nước lướt tr&ecirc;n ngọn s&oacute;ng một c&aacute;ch an to&agrave;n, kh&ocirc;ng lo bị s&oacute;ng biển cuốn đi như nhiều b&atilde;i biển kh&aacute;c.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Khung cảnh bình minh và hoàng hôn tuyệt đẹp tại Biển Ba Động\" src=\"https://dulichtoivaban.com/wp-content/uploads/2022/08/Khung-canh-binh-minh-va-hoang-hon-tuyet-dep-tai-Bien-Ba-Dong.jpg\" style=\"height:710px; width:947px\" title=\"Khám phá Khu du lịch Biển Ba Động - Trà Vinh 5\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khung cảnh b&igrave;nh minh v&agrave; ho&agrave;ng h&ocirc;n tuyệt đẹp tại Biển Ba Động.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khi thấm mệt, bạn c&ugrave;ng gia đ&igrave;nh c&oacute; thể lựa chọn g&oacute;c thuận tiện trong nh&agrave; h&agrave;ng hoặc d&atilde;y ghế bố dọc theo bờ k&egrave; nh&igrave;n ra mặt biển khơi xa t&iacute;t tắp, nơi c&oacute; những đo&agrave;n thuyền đ&aacute;nh c&aacute; như những chấm đen nho nhỏ di động ph&iacute;a ch&acirc;n trời v&agrave; tận hưởng những l&agrave;n gi&oacute; biển thổi v&agrave;o m&aacute;t rượi.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&oacute; thời gian, du kh&aacute;ch thả bộ dọc theo những lối m&ograve;n quanh co, kh&uacute;c khuỷu theo ch&acirc;n c&aacute;c động c&aacute;t, xuy&ecirc;n qua v&agrave; h&ograve;a v&agrave;o những h&agrave;ng phi lao uốn lượn theo chiều gi&oacute; m&agrave; qu&ecirc;n đi những mệt mỏi, lo toan của cuộc sống bộn bề thường nhật.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ng&agrave;y nay, với những tiềm năng v&agrave; thế mạnh đặc th&ugrave; kh&ocirc;ng đ&acirc;u c&oacute; được, v&ugrave;ng ven biển Trường Long H&ograve;a, Duy&ecirc;n Hải trở th&agrave;nh v&ugrave;ng kinh tế trọng điểm của tỉnh Tr&agrave; Vinh, với nhiều c&ocirc;ng tr&igrave;nh trọng điểm quốc gia ra đời, đi v&agrave;o hoạt động như Trung t&acirc;m Nhiệt điện Duy&ecirc;n Hải, Luồng t&agrave;u biển trọng tải lớn ra v&agrave;o s&ocirc;ng Hậu&hellip;</span></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&ocirc;ng Tr&igrave;nh Điện Gi&oacute; Hàn Qu&ocirc;́c ở Biển Ba Động</span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đặc bi&ecirc;̣t nay c&oacute; th&ecirc;m&nbsp;<strong>C&ocirc;ng Tr&igrave;nh Điện Gi&oacute; Hàn Qu&ocirc;́c</strong>&nbsp;nằm c&aacute;ch biển Ba Động 1km hướng về x&atilde; D&acirc;n Th&agrave;nh. Với c&acirc;y cầu m&agrave;u v&agrave;ng nổi bật và 12 tua-bin gi&oacute; g&oacute;p th&ecirc;m cảnh quan đẹp cho biển Ba Động khi&ecirc;́n nơi đ&acirc;y trở thành địa điểm sống ảo hot nhất Tr&agrave; Vinh.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Cầu vàng điện gió tại Duyên Hải - Trà Vinh\" src=\"https://dulichtoivaban.com/wp-content/uploads/2022/08/Cau-vang-dien-gio-tai-Duyen-Hai-Tra-Vinh.png\" style=\"height:710px; width:1080px\" title=\"Khám phá Khu du lịch Biển Ba Động - Trà Vinh 6\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cầu v&agrave;ng điện gi&oacute; tại Duy&ecirc;n Hải &ndash; Tr&agrave; Vinh.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mỗi buổi bình minh hay ho&agrave;ng h&ocirc;n, đứng ở b&atilde;i biển Ba Động bạn sẽ nh&igrave;n thấy vẻ đẹp của cầu dẫn d&agrave;i hơn 4 c&acirc;y số dẫn ra nơi lắp đặt c&aacute;c trụ tua-bin gi&oacute; nối liền nhau. Ánh s&aacute;ng mặt trời chi&ecirc;́u lung linh tạo n&ecirc;n khung cảnh v&ocirc; cùng lãng mạn của những c&aacute;nh quạt khổng lồ giữa biển bao la.</span></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cẩm nang du lịch Biển Ba Động Tr&agrave; Vinh</span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Khu du lịch Biển Ba Động</strong>&nbsp;l&agrave; trọng t&acirc;m của một chuỗi c&aacute;c địa chỉ du lịch tiềm năng ven biển Tr&agrave; Vinh như:&nbsp;<em>Tr&uacute;c L&acirc;m Thiền viện, khu Di t&iacute;ch Bến Tiếp nhận vũ kh&iacute; Cồn T&agrave;u, Đ&igrave;nh miếu Cồn Trứng, Hải đăng V&agrave;m L&aacute;ng Nước, Lầu B&agrave; Thượng động Cố hỷ nương nương, mộ cổ Ba Động</em>&hellip; rất thuận tiện cho việc tham quan, t&igrave;m hiểu, nghi&ecirc;n cứu hoặc đ&aacute;p ứng nhu cầu t&acirc;m linh của du kh&aacute;ch.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Từ Ba Động, du kh&aacute;ch c&oacute; thể tham quan nhiều địa danh nổi tiếng kh&aacute;c như&nbsp;<em>cửa biển Cung Hầu</em>&nbsp;(gi&aacute;p với Tiền Giang),&nbsp;<em>cửa Định An&nbsp;</em>(gi&aacute;p S&oacute;c Trăng) v&agrave; chi&ecirc;m ngưỡng vẻ đẹp kiến tr&uacute;c cổ k&iacute;nh của một số nh&agrave; thờ đ&atilde; được x&acirc;y dựng từ thời Ph&aacute;p thuộc, như&nbsp;<em>nh&agrave; thờ Giồng R&ugrave;m, nh&agrave; thờ Phước Hảo</em>.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Du kh&aacute;ch sẽ c&oacute; dịp t&igrave;m hiểu bề d&agrave;y lịch sử, chiều s&acirc;u văn h&oacute;a của v&ugrave;ng đất ven biển Trường Long H&ograve;a, Duy&ecirc;n Hải, Tr&agrave; Vinh v&agrave; thưởng thức c&aacute;c sản phẩm đặc trưng của c&aacute;c l&agrave;ng nghề truyền thống nổi tiếng một thời như&nbsp;<em>L&agrave;ng Đ&aacute;y biển Động Cao, L&agrave;ng Muối Cồn C&ugrave;, L&agrave;ng Dưa hấu Ba Động</em>&hellip;</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Toàn cảnh điện gió Hàn Quốc tại Duyên Hải - Trà Vinh . Ảnh Võ Trường An\" src=\"https://dulichtoivaban.com/wp-content/uploads/2022/08/Toa%CC%80n-ca%CC%89nh-die%CC%A3n-gio%CC%81-Ha%CC%80n-Quo%CC%81c-tai-Duyen-Hai-Tra-Vinh-.-Anh-Vo-Truong-An.jpg\" style=\"height:710px; width:1261px\" title=\"Khám phá Khu du lịch Biển Ba Động - Trà Vinh 7\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Toàn cảnh đi&ecirc;̣n gió Hàn Qu&ocirc;́c tại Duy&ecirc;n Hải &ndash; Tr&agrave; Vinh . Ảnh V&otilde; Trường An.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nếu dừng ch&acirc;n l&acirc;u hơn, du kh&aacute;ch cũng c&oacute; thể l&ecirc;n thuyền l&agrave;m chuyến du ngoạn dọc theo chiều d&agrave;i Luồng T&agrave;u biển trọng tải lớn ra v&agrave;o s&ocirc;ng Hậu, ngắm nh&igrave;n những chiếc t&agrave;u viễn dương mang nhiều quốc tịch tr&ecirc;n thế giới đến với c&aacute;c cảng khắp khu vực Đồng bằng s&ocirc;ng Cửu Long, ngắm nh&igrave;n&nbsp;<strong>Trung t&acirc;m Nhiệt điện Duy&ecirc;n Hải</strong>&nbsp;v&agrave; những chiếc t&agrave;u vận tải than nguy&ecirc;n liệu neo đậu chờ bốc dở.</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&oacute; thời gian hơn nữa, du kh&aacute;ch l&agrave;m chuyến du lịch mạo hiểm vượt s&oacute;ng biển ra tận những thon đ&aacute;y h&agrave;ng khơi, nơi c&oacute; những ch&agrave;ng ngư d&acirc;n dũng cảm, ng&agrave;y đ&ecirc;m đối đầu c&ugrave;ng gi&oacute; to s&oacute;ng cả tr&ecirc;n những chiếc ch&ograve;i canh b&eacute; x&iacute;u, g&oacute;p phần l&agrave;m gi&agrave;u đẹp qu&ecirc; hương.</span></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ẩm thực tại Biển Ba Động Tr&agrave; Vinh</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đến với Biển Ba Động, d&ugrave; l&agrave; m&ugrave;a nam hay m&ugrave;a chướng, d&ugrave; tại nh&agrave; h&agrave;ng hay đi dạo quanh c&aacute;c x&oacute;m hạ bạc, du kh&aacute;ch c&oacute; thể tự tay lựa chọn nhiều mặt h&agrave;ng thủy hải sản tươi sống từ những chiếc ghe đ&aacute;nh c&aacute; vừa quay mũi v&agrave;o bờ cho bữa ăn của m&igrave;nh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Chù ụ rang me – Món ăn ngon nức tiếng Trà Vinh\" src=\"https://dulichtoivaban.com/wp-content/uploads/2022/08/Chu-u-rang-me-%E2%80%93-Mon-an-ngon-nuc-tieng-Tra-Vinh.jpg\" style=\"height:710px; width:1264px\" title=\"Khám phá Khu du lịch Biển Ba Động - Trà Vinh 8\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ch&ugrave; ụ rang me &ndash; M&oacute;n ăn ngon nức tiếng Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cao cấp hơn một ch&uacute;t, du kh&aacute;ch c&oacute; thể đặt sẵn hay nhờ nh&acirc;n vi&ecirc;n nh&agrave; h&agrave;ng hướng dẫn để tự tay chế biến những m&oacute;n đặc sản nổi tiếng của l&agrave;ng ven biển địa phương, m&agrave; mỗi m&oacute;n ăn đều gắn một giai thoại, truyền thuyết hấp dẫn như:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">M&oacute;n c&aacute; k&egrave;o kho gợt chấm nước mắm rươi, loại nước chấm m&agrave; những năm b&ocirc;n tẩu được người d&acirc;n địa phương cung tiến, ch&uacute;a Nguyễn &Aacute;nh nếm thử một lần kh&ocirc;ng thể qu&ecirc;n được suốt đời n&ecirc;n ban cho mỹ danh l&agrave; &ldquo;nước mắm ngự&rdquo;;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">M&oacute;n đu&ocirc;n ch&agrave; l&agrave; b&eacute;o ngậy hay t&ocirc;m thẻ, cua biển hấp bia chấm muối ti&ecirc;u chanh;</span></span></li>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">M&oacute;n ch&ugrave; ụ rang me tuy c&oacute; phần d&acirc;n d&atilde; nhưng ngon kh&ocirc;ng lẫn v&agrave;o đ&acirc;u được, vừa ăn vừa thương cho ch&agrave;ng trai biển hiền l&agrave;nh, tốt bụng m&agrave; thật th&agrave; n&ecirc;n thua thiệt đ&agrave;nh h&oacute;a kiếp th&agrave;nh con ch&ugrave; ụ quanh năm b&aacute;m chặt gốc dừa nước&hellip;</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Ch&uacute;c c&aacute;c bạn c&oacute; những chuyến đi thật vui v&agrave; &yacute; nghĩa!</em></strong></span></span></p>\r\n', 'Biển Ba Động thuộc địa phận xã Trường Long Hoà , huyện Duyên Hải. Cách trung tâm TP. Trà Vinh chừng.', '0000-00-00', 3, ' Biển Ba Động gây ấn tượng bởi 25 tua bin gió, mỗi trụ cao khoảng 105m với 3 cách quạt đặc trưng. Cùng với nước biển trong xanh ngút ngàn.', ' Có đường bờ biển dài hơn 10km từ ấp Nhà Mát tới ấp Cồn Trứng. ', 15);
INSERT INTO `travelviewing` (`id`, `title`, `iva1`, `iva2`, `iva3`, `content`, `code`, `Dday`, `time`, `startingplace`, `ToLocation`, `Numberofseats`) VALUES
(12, 'Thiền Viện Trúc Lâm', './upload/image/thiền viện trúc lâm.jpg', './upload/image/thienvientruclamtravinh5.jpg', './upload/image/tvtl.jpg', '<h1><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">THIỀN VIỆN TR&Uacute;C L&Acirc;M TR&Agrave; VINH</span></span></h1>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thiền viện Tr&uacute;c l&acirc;m Tr&agrave; Vinh l&agrave; ng&ocirc;i thiền viện đầu ti&ecirc;n thuộc ph&aacute;i Tr&uacute;c l&acirc;m Y&ecirc;n Tử của tỉnh Tr&agrave; Vinh. Thiền Viện Tr&uacute;c L&acirc;m Tr&agrave; Vinh tọa lạc tại ấp Kho&aacute;n Tiều, x&atilde; Trường Long H&ograve;a, thị x&atilde; Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh với diện t&iacute;ch gần 10 ha trong quần thể Khu du lịch sinh th&aacute;i rừng ngập mặn biển Ba Động.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/thienvientruclamtravinh-1.jpg\" style=\"height:750px; width:1000px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thiền viện Tr&uacute;c l&acirc;m Tr&agrave; Vinh s&aacute;t biển Ba Động.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thiền viện Tr&uacute;c L&acirc;m Tr&agrave; Vinh c&oacute; c&aacute;c hạng mục ch&iacute;nh như: Ch&iacute;nh điện, nh&agrave; Tổ, lầu chu&ocirc;ng, lầu trống, cổng tam quan, nh&agrave; nghỉ ch&acirc;n, t&ocirc;n tượng Quan &Acirc;m lộ thi&ecirc;n, miếu thờ mẹ &Acirc;u Cơ, giảng đường, trai đường, nh&agrave; trụ tr&igrave;, nh&agrave; kh&aacute;ch, thư viện, Tăng x&aacute;, Ni x&aacute;, nh&agrave; tr&ugrave;, th&aacute;p nước,&hellip; &nbsp;C&aacute;c c&ocirc;ng tr&igrave;nh được bố tr&iacute; h&agrave;i h&ograve;a với thi&ecirc;n nhi&ecirc;n.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/thienvientruclamtravinh1.jpg\" style=\"height:750px; width:1068px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cổng tam quan</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">To&agrave;n bộ c&aacute;c kiến tr&uacute;c tại Thiền viện Tr&uacute;c L&acirc;m Tr&agrave; Vinh được thiết kế v&agrave; x&acirc;y dựng theo m&ocirc; h&igrave;nh c&aacute;c ng&ocirc;i ch&ugrave;a Phật gi&aacute;o Việt Nam thời L&yacute; &ndash; Trần (thế kỷ XII đến thế kỷ XIV), m&agrave; điển h&igrave;nh l&agrave; c&aacute;c m&aacute;i ng&oacute;i dạng hai tầng. C&aacute;c đầu đao cuối m&aacute;i được vuốt cong nhẹ nh&agrave;ng, thể hiện t&iacute;nh khi&ecirc;m cung, h&agrave;i h&ograve;a với tự nhi&ecirc;n của d&acirc;n tộc.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/thienvientruclamtravinh5.jpg\" style=\"height:750px; width:1333px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khu&ocirc;n vi&ecirc;n rộng r&atilde;i</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ng&ocirc;i ch&aacute;nh điện c&oacute; hướng nh&igrave;n ra biển Đ&ocirc;ng, khu&ocirc;n vi&ecirc;n được bao bọc bởi nhiều động c&aacute;t cao, phủ đầy dương xanh ng&aacute;t, r&igrave; r&agrave;o c&ugrave;ng s&oacute;ng biển.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/thienvientruclamtravinh2.jpg\" style=\"height:750px; width:1080px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ch&aacute;nh điện</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thiền viện Tr&uacute;c L&acirc;m đang giữ nhiều kỷ lục trong số c&aacute;c tự viện Phật gi&aacute;o tr&ecirc;n địa b&agrave;n Tr&agrave; Vinh như ng&ocirc;i ch&ugrave;a gần biển Đ&ocirc;ng nhất, to&agrave;n bộ c&aacute;c kiến tr&uacute;c gần với kiến tr&uacute;c truyền thống d&acirc;n tộc nhất, ng&ocirc;i Ch&aacute;nh điện to nhất, tượng Phật tổ to nhất, tượng Qu&aacute;n Thế &acirc;m to nhất, đại hồng chung lớn nhất, giảng đường c&oacute; sức chứa lớn nhất, khoảng s&acirc;n ph&iacute;a trước Ch&aacute;nh điện rộng lớn nhất&hellip;</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/thienvientruclamtravinh6.jpg\" style=\"height:750px; width:1016px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kiến tr&uacute;c truyền thống thời L&yacute; Trần</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/thienvientruclamtravinh.jpg\" style=\"height:750px; width:1185px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Th&aacute;p chu&ocirc;ng</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/thienvientruclamtravinh3.jpg\" style=\"height:750px; width:1000px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Th&aacute;p trống</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thiền Viện Tr&uacute;c L&acirc;m Tr&agrave; Vinh được x&acirc;y dựng với mong muốn kh&ocirc;i phục những gi&aacute; trị văn h&oacute;a phật gi&aacute;o truyền thống theo Thiền ph&aacute;i Tr&uacute;c L&acirc;m Y&ecirc;n Tử của Phật ho&agrave;ng Trần Nh&acirc;n T&ocirc;ng, đồng thời tạo điều kiện cho tăng ni, phật tử hữu duy&ecirc;n tu thiền.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://thamhiemmekong.com/wp-content/uploads/2020/06/thienvientruclamtravinh4.jpg\" style=\"height:750px; width:1333px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tượng Bồ t&aacute;t Qu&aacute;n Thế &Acirc;m Đ&ocirc;ng Hải</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thiền viện Tr&uacute;c l&acirc;m Tr&agrave; Vinh sẽ l&agrave; điểm nhấn du lịch của đồng bằng s&ocirc;ng Cửu Long n&oacute;i chung v&agrave; tỉnh Tr&agrave; Vinh n&oacute;i ri&ecirc;ng, g&oacute;p phần đa dạng sản phẩm du lịch trong quần thể khu du lịch sinh th&aacute;i rừng ngập mặn biển Ba Động, tạo cơ hội cho ng&agrave;nh&nbsp;<a href=\"https://thamhiemmekong.com/thong-tin-du-lich-mien-tay/dia-diem-du-lich-tra-vinh-tuong-nhat-dinh-phai-di.html\" target=\"_top\" title=\"du lịch Trà Vinh\">du lịch Tr&agrave; Vinh</a>&nbsp;ph&aacute;t triển.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Ch&uacute;c c&aacute;c bạn c&oacute; những chuyến đi thật vui v&agrave; &yacute; nghĩa!</em></strong></span></span></p>\r\n', 'Thiện viện Trúc Lâm toạ lạc tại ấp Khoán Tiều, xã Trường Long Hoà, thị xã Duyên Hải, cách trung tâm.', '0000-00-00', 3, 'Thiền viện Trúc Lâm có các hạng mục chính như: Chính điện, nhà Tổ, lầu chuông, cổng tam quan, Tăng xá, nhà trù, tháp nước,.... được bố trí hài hoà với thiên nhiên.', 'gần 10ha,mặt tiền nhìn ra biển Ba Động bao la và rộng lớn.', 15),
(16, 'Nhà thờ Mặc Bắc', './upload/image/nhatho.jpg', './upload/image/nhatho1.jpg', './upload/image/nha tho.jpg', '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; thờ Mặc Bắc</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Quốc lộ 60, Long Thới, Tiểu Cần, Tr&agrave; Vinh</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a href=\"https://goo.gl/maps/JJQusWnKAufBwrNZ7\" target=\"”_blank”\">T&igrave;m đường đi</a></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; thờ C&ocirc;ng gi&aacute;o Mặc Bắc, tọa lạc tại thị trấn Cầu Quan, huyện Tiểu Cần, c&aacute;ch th&agrave;nh phố Tr&agrave; Vinh 30 km v&agrave; c&aacute;ch thị trấn Tiểu Cần 7 km đều theo hướng t&acirc;y nam, c&aacute;ch bờ tr&aacute;i s&ocirc;ng Hậu 3 km về hướng đ&ocirc;ng. Đ&acirc;y l&agrave; ng&ocirc;i Th&aacute;nh đường c&oacute; qui m&ocirc; lớn nhất, ni&ecirc;n đại cao nhất thuộc một gi&aacute;o xứ C&ocirc;ng gi&aacute;o kh&ocirc;ng chỉ tr&ecirc;n địa b&agrave;n tỉnh Tr&agrave; Vinh, m&agrave; c&ograve;n l&agrave; một trong những gi&aacute;o xứ c&oacute; đ&ocirc;ng gi&aacute;o d&acirc;n nhất khu vực Đồng bằng s&ocirc;ng Cửu Long.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"http://nhathoconggiaovietnam.com/storage/photos/1/071747-facebook-h-a-nguy-n---nh--th--m-c-b-c.jpg\" style=\"height:750px; width:1125px\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khu&ocirc;n vi&ecirc;n Nh&agrave; thờ Mặc Bắc rộng hơn 1 ha, được che m&aacute;t bởi những h&agrave;ng sao cổ thụ, nằm ở vị tr&iacute; trung t&acirc;m con giồng đất c&aacute;t cao r&aacute;o, cổng ch&iacute;nh nh&igrave;n ra ng&atilde; ba giao nhau giữa quốc lộ 60 v&agrave; con đường dẫn ra chợ v&agrave; bến t&agrave;u Cầu Quan sầm uất.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; thờ Mặc Bắc l&agrave; một c&ocirc;ng tr&igrave;nh kiến tr&uacute;c nghệ thuật t&ocirc;n gi&aacute;o nổi tiếng, l&agrave; sự kết hợp phong c&aacute;ch kiến tr&uacute;c La M&atilde;, kiến tr&uacute;c Ph&aacute;p v&agrave; kiến tr&uacute;c truyền thống người Việt cuối thế kỷ XIX.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"http://nhathoconggiaovietnam.com/storage/photos/1/nha-tho-mac-bac-2.jpg\" style=\"height:750px; width:999px\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">V&agrave;o thập ni&ecirc;n 1860, sau khi thực d&acirc;n Ph&aacute;p x&acirc;m chiếm v&agrave; cai trị v&ugrave;ng đất Nam bộ, c&aacute;c gi&aacute;o xứ C&ocirc;ng gi&aacute;o ra hoạt động c&ocirc;ng khai v&agrave; gi&aacute;o xứ Mặc Bắc ph&aacute;t triển dần về ph&iacute;a trung t&acirc;m con giồng c&aacute;t cao r&aacute;o, cặp s&ocirc;ng Cần Chong, thuận tiện giao th&ocirc;ng thủy bộ để ph&aacute;t triển. Ch&iacute;nh quyền thực d&acirc;n x&acirc;y dựng một cầu cảng nhỏ tr&ecirc;n bến Cần Chong để c&aacute;c quan đầu tỉnh người Ph&aacute;p từ Tr&agrave; Vinh c&oacute; thể dễ d&agrave;ng tới thăm viếng, xem lễ. Từ đ&oacute;, địa danh Cầu Quan h&igrave;nh th&agrave;nh v&agrave; gi&aacute;o xứ Mặc Bắc c&ograve;n c&oacute; t&ecirc;n gọi kh&aacute;c l&agrave; gi&aacute;o xứ Cầu Quan.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"http://nhathoconggiaovietnam.com/storage/photos/1/61f6e57a3b20d90d2fd70957f7665652.jpg\" style=\"height:750px; width:1000px\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; thờ Mặc Bắc được khởi c&ocirc;ng sau khi Vương cung Th&aacute;nh đường hay Nh&agrave; thờ Đức B&agrave; S&agrave;i G&ograve;n ho&agrave;n th&agrave;nh 6 năm, tức năm 1886. Bản vẽ của nh&agrave; thờ Mặc Bắc ch&iacute;nh l&agrave; bản vẽ của Nh&agrave; thờ Đức B&agrave;, do kiến tr&uacute;c sư J.Bourad thiết kế, được thu nhỏ lại theo tỷ lệ 70/100. Từ bản vẽ n&agrave;y, vi&ecirc;n kỹ sư người Ph&aacute;p Errard chịu tr&aacute;ch nhiệm chung nhưng người trực tiếp chỉ huy, đ&ocirc;n đốc việc thi c&ocirc;ng l&agrave; &ocirc;ng Tr&ugrave;m xứ Nguyễn Văn Thăng. To&agrave;n bộ vật tư từ c&aacute;c v&igrave; k&egrave;o v&agrave; bộ khung m&aacute;i bằng th&eacute;p, xi măng, ng&oacute;i, k&iacute;nh m&agrave;u, gạch x&acirc;y, gạch l&oacute;t nền, bộ chu&ocirc;ng đồng 6 &acirc;m (đ&ocirc;, r&ecirc;, si, pha, sol, la)&hellip; đều được sản xuất tại Ph&aacute;p vận chuyển bằng t&agrave;u thủy sang. Tuy nhi&ecirc;n điều đặc biệt hơn cả l&agrave; đội ngũ thi c&ocirc;ng Nh&agrave; thờ đều l&agrave; người Việt. Điều n&agrave;y cho thấy tr&igrave;nh độ sản xuất, tay nghề của người Tr&agrave; Vinh thời bấy giờ rất cao, c&oacute; thể đ&aacute;p ứng được c&ocirc;ng tr&igrave;nh của thế kỉ như vậy.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"http://nhathoconggiaovietnam.com/storage/photos/1/l_3982ac6bc3786d6d94015d0ee26c8390.jpg\" style=\"height:750px; width:1000px\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khi thiết kế m&aacute;i ng&oacute;i, kiến tr&uacute;c sư J.Bourad đ&atilde; th&agrave;nh c&ocirc;ng trong việc kết hợp h&agrave;i h&ograve;a giữa kiểu m&aacute;i ng&oacute;i cung đ&igrave;nh Việt với m&aacute;i ng&oacute;i Nh&agrave; thờ phương T&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tr&ecirc;n tường ph&iacute;a dưới v&agrave; tr&ecirc;n cao của Nh&agrave; thờ Mặc Bắc c&oacute; c&aacute;c cửa sổ được lắp đặt c&aacute;c t&aacute;c phẩm nghệ thuật bằng k&iacute;nh m&agrave;u, c&oacute; nội dung diễn tả c&aacute;c vị Th&aacute;nh v&agrave; sự kiện trong Kinh Th&aacute;nh, nhưng cũng xen kẽ rất nhiều họa tiết v&agrave; h&igrave;nh tượng phương Đ&ocirc;ng. Hệ thống k&iacute;nh m&agrave;u được thiết kế rất đặc sắc v&agrave; phối s&aacute;ng h&agrave;i h&ograve;a, tạo n&ecirc;n một hiệu ứng &aacute;nh s&aacute;ng tuyệt vời. Trong l&ograve;ng Nh&agrave; thờ, một l&agrave;n &aacute;nh s&aacute;ng nhẹ nh&agrave;ng, tạo bầu kh&ocirc;ng kh&iacute; trang nghi&ecirc;m, an b&igrave;nh... &Aacute;nh s&aacute;ng huyền ảo cũng l&agrave;m cho c&aacute;c chi tiết kiến tr&uacute;c, nội thất b&ecirc;n trong Th&aacute;nh đường nổi bật v&agrave; đẹp hơn.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"http://nhathoconggiaovietnam.com/storage/photos/1/178658330_4049970875087782_8312668750418043260_n.jpg\" style=\"height:750px; width:1000px\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B&agrave;n thờ ph&iacute;a cung Th&aacute;nh trong Nh&agrave; thờ Mặc Bắc được l&agrave;m bằng đ&aacute; cẩm thạch nguy&ecirc;n khối với c&aacute;c thi&ecirc;n thần được tạc tr&ecirc;n khối đ&aacute; b&agrave;n thờ. Bệ đỡ b&agrave;n thờ cũng l&agrave; t&aacute;c phẩm đi&ecirc;u khắc mỹ thuật tuyệt đẹp diễn tả c&aacute;c sự kiện trong Kinh Th&aacute;nh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">&Aacute;nh s&aacute;ng từ những chiếc đ&egrave;n ch&ugrave;m được thiết kế với những hoa văn theo kiểu Roman - Gotich tạo n&ecirc;n một kh&ocirc;ng gian lung linh, trang trọng v&agrave; th&aacute;nh thiện.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"http://nhathoconggiaovietnam.com/storage/photos/1/l_100e30a278479cda7a8fd0260d05ce03.jpg\" style=\"height:750px; width:563px\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&iacute;a trước mặt tiền l&agrave; một sảnh lộ thi&ecirc;n c&oacute; chiều d&agrave;i bằng với chiều ngang của ng&ocirc;i Nh&agrave; thờ, cao 1,2 m hơi thấp hơn nền nh&agrave; thờ một ch&uacute;t, rộng 4 m. Đ&acirc;y l&agrave; nơi diễn ra c&aacute;c nghi thức ngo&agrave;i trời trong những dịp th&aacute;nh lễ.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"http://nhathoconggiaovietnam.com/storage/photos/1/071828-internet----th-nh----ng-nh--th--m-c-b-c.jpg\" style=\"height:750px; width:1000px\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;ch một khoảng s&acirc;n ph&iacute;a trước l&agrave; hồ nước h&igrave;nh vu&ocirc;ng c&oacute; diện t&iacute;ch hơn 150 m2 vừa tạo cảnh n&ocirc;ng th&ocirc;n Việt Nam vừa gi&uacute;p cho gi&aacute;o d&acirc;n từ c&aacute;c th&ocirc;n x&oacute;m, băng đồng đi bộ đến rửa ch&acirc;n tay sạch sẽ trước khi v&agrave;o Nh&agrave; thờ dự lễ.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; xứ l&agrave; một kiến tr&uacute;c hai tầng, với cầu thang gỗ, s&agrave;n lầu gỗ được x&acirc;y dựng ph&iacute;a đ&ocirc;ng ng&ocirc;i Th&aacute;nh đường, d&agrave;nh l&agrave;m nơi ở cho c&aacute;c vị linh mục, thầy gi&uacute;p việc v&agrave; l&agrave; nơi hội họp của ban quới chức gi&aacute;o xứ.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Lễ Phục sinh năm 1888, sau hơn 2 năm thi c&ocirc;ng, Nh&agrave; thờ Mặc Bắc được kh&aacute;nh th&agrave;nh với diện t&iacute;ch 24 m x 60 m v&agrave; hai th&aacute;p chu&ocirc;ng cao 32 m được. Đ&acirc;y l&agrave; nh&agrave; thờ C&ocirc;ng gi&aacute;o lớn thứ hai Nam bộ v&agrave;o cuối thế kỷ XIX v&agrave; nửa đầu thế kỷ XX, chỉ sau Nh&agrave; thờ Đức B&agrave; S&agrave;i G&ograve;n n&ecirc;n được mệnh danh l&agrave; &ldquo;Vương cung Th&aacute;nh đường miền T&acirc;y Nam bộ&rdquo;.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"http://nhathoconggiaovietnam.com/storage/photos/1/l_a40537f48520dac5bba6e11570369d35.jpg\" style=\"height:750px; width:1000px\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; thờ Mặc Bắc l&agrave; kiến tr&uacute;c cổ nhất vẫn c&ograve;n tồn tại tr&ecirc;n địa b&agrave;n Tr&agrave; Vinh v&agrave; được đ&aacute;nh gi&aacute; l&agrave; Nh&agrave; thờ đẹp, cổ k&iacute;nh bậc nhất Miền T&acirc;y. Sau hơn 130 năm, theo thời gian Nh&agrave; thờ Mặc Bắc cũng đ&atilde; trải qua nhiều lần tr&ugrave;ng tu, t&ocirc;n tạo nhưng vẫn giữ được d&aacute;ng vẻ như thiết kế ban đầu. Hồ nước đ&atilde; được san lấp để tr&aacute;nh nguy hiểm cho trẻ nhỏ v&agrave; tạo th&ecirc;m diện t&iacute;ch phần s&acirc;n trước cho c&aacute;c hoạt động kh&aacute;c. Một số hạng mục được x&acirc;y dựng mới, đ&aacute;p ứng y&ecirc;u cầu ph&aacute;t triển của một gi&aacute;o xứ c&oacute; lịch sử h&igrave;nh th&agrave;nh, ph&aacute;t triển l&acirc;u d&agrave;i nhưng vẫn giữ được h&agrave;i h&ograve;a trong cảnh quan chung.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mặc Bắc vẫn vững v&agrave;ng,thu h&uacute;t như thuở ban đầu v&agrave; xứng đ&aacute;ng với c&aacute;i t&ecirc;n Vương cung Th&aacute;nh đường miền T&acirc;y Nam Bộ.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Ch&uacute;c c&aacute;c bạn c&oacute; những chuyến đi thật vui v&agrave; &yacute; nghĩa!</em></strong></span></span></p>\r\n', 'Nhà thờ Mặc Bắc nằm tại thị trấn Cầu Quan, huyện Tiểu Cần, cách TP.Trà Vinh  khoảng 30km.', '0000-00-00', 3, ' Nhà thờ Mặc Bắc được che mát bởi những hàng cây sao cổ thụ. Cách một khoảng sân phía trước là hồ nước hình vuông tạo nên cảnh thôn quê gần gũi.', 'Nhà thờ Mặc Bắc được khánh thành với diện tích 24×60m và hai tháp chuông cao 32m.', 15),
(17, 'khu du lịch sinh thái Rừng Đước', './upload/image/rừng đước2.jpg', './upload/image/rừng đước1.jpg', './upload/image/rừng đước.jpg', '<h3><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1. Tọa độ khu du lịch sinh th&aacute;i Rừng Đước</strong></span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Địa chỉ: x&atilde; Long Kh&aacute;nh, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Rừng đước nằm kh&ocirc;ng qu&aacute; xa trung t&acirc;m tỉnh Tr&agrave; Vinh, chỉ c&aacute;ch khu du lịch Ba Động nổi tiếng chỉ khoảng 7 km n&ecirc;n kh&aacute; thuận tiện cho việc đi lại của du kh&aacute;ch. Khu rừng n&agrave;y c&oacute; nhiều loại c&acirc;y đặc trưng của hệ sinh th&aacute;i ngập mặn như đước, mắm, ch&agrave; l&agrave; gai, vẹt... trong đ&oacute; chiếm số lượng nhiều nhất l&agrave; c&acirc;y đước. Đ&acirc;y l&agrave; loại c&acirc;y vừa c&oacute; t&aacute;c dụng ngăn chặn sự x&acirc;m nhập của nước biển v&agrave;o đất liền vừa l&agrave; nơi sinh sống của nhiều loại động vật đặc trưng của rừng ngập mặn. V&igrave; vậy m&agrave; khu du lịch ki&ecirc;m khu bảo tồn n&agrave;y c&oacute; gi&aacute; trị sinh th&aacute;i cao.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hơn thế nữa, c&aacute;nh rừng đước rộng khoảng hơn 200 ha c&ograve;n nằm trong hệ thống rừng ngập mặn ven biển T&acirc;y Nam Bộ n&ecirc;n được nh&agrave; nước chăm s&oacute;c v&agrave; bảo tồn kh&aacute; cẩn thận.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"khu-du-lich-sinh-thai-rung-duoc-32773.jpg (680×453)\" src=\"https://toplist.vn/images/800px/khu-du-lich-sinh-thai-rung-duoc-32773.jpg\" style=\"height:800px; width:1201px\" /><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khu du lịch sinh th&aacute;i nổi tiếng. Ảnh: reviewkinhnghiemdulichthamquandiphuotvietnam</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">2. Hướng dẫn đường đi đến khu du lịch sinh th&aacute;i Rừng Đước</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tr&agrave; Vinh chỉ nằm c&aacute;ch th&agrave;nh phố Hồ Ch&iacute; Minh khoảng 170k n&ecirc;n du kh&aacute;ch c&oacute; thể đi đến khu du lịch Rừng Đước thuộc địa phận tỉnh n&agrave;y bằng xe m&aacute;y hoặc xe kh&aacute;ch. C&aacute;ch di chuyển cụ thể như sau:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Đi bằng xe m&aacute;y: Xuất ph&aacute;t từ S&agrave;i G&ograve;n, bạn đi tới B&igrave;nh Ch&aacute;nh rồi rẽ v&agrave;o Quốc lộ 1 v&agrave; tiếp tục đi theo hướng đi qua cầu Bến Lức đến th&agrave;nh phố T&acirc;n An của tỉnh Long An. Từ đ&acirc;y, du kh&aacute;ch đi tiếp đến th&agrave;nh phố Mỹ Tho, dọc theo quốc lộ 60 qua cầu H&agrave;m Lu&ocirc;ng l&agrave; sẽ đến với huyện Mỏ C&agrave;y. Tiếp tục rẽ v&agrave;o Quốc lộ 53 v&agrave; đi thẳng l&agrave; sẽ tới th&agrave;nh phố Tr&agrave; Vinh rồi đấy. Từ trung t&acirc;m th&agrave;nh phố, bạn chỉ cần đi khoảng 51km nữa l&agrave; tới được khu du lịch Rừng Đước.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Đi bằng xe kh&aacute;ch: H&agrave;nh tr&igrave;nh kh&aacute; đơn giản v&igrave; bạn chỉ cần ra bến xe miền Đ&ocirc;ng, chọn mua v&eacute; xe đi Tr&agrave; Vinh với gi&aacute; v&eacute; tr&ecirc;n dưới 100.000đ/người. Khi đến bến xe th&igrave; bạn chọn c&aacute;ch di chuyển bằng taxi hoặc xe &ocirc;m l&agrave; sẽ đến được địa điểm du lịch Tr&agrave; Vinh hấp dẫn mang t&ecirc;n Rừng Đước rồi đấy.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Khu du lịch Rừng Đước Trà Vinh - Sông nước hữu tình\" src=\"https://luhanhvietnam.com.vn/du-lich/vnt_upload/news/08_2023/song_nuoc_huu_tinh.jpg\" style=\"height:800px; width:1421px\" title=\"Khu du lịch Rừng Đước ở Trà Vinh\" />S&ocirc;ng nước hữu t&igrave;nh. Ảnh: VNAirline</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thời điểm l&yacute; tưởng đi&nbsp;<strong>khu du lịch Rừng Đước Tr&agrave; Vinh</strong></span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tr&agrave; Vinh thuộc khu vực đồng bằng s&ocirc;ng Cửu Long n&ecirc;n cũng c&oacute; kh&iacute; hậu nhiệt đới &ocirc;n h&ograve;a đặc trưng của v&ugrave;ng n&agrave;y. Cũng như c&aacute;c tỉnh Bến Tre, Tiền Giang, Long An, Tr&agrave; Vinh quanh năm m&aacute;t mẻ n&ecirc;n du kh&aacute;ch c&oacute; thể đến với khu sinh th&aacute;i Rừng Đước, ao B&agrave; Om,&nbsp;<a href=\"https://luhanhvietnam.com.vn/du-lich/cu-lao-long-tri-an-minh-giua-song-nuoc-tra-vinh.html\"><em><strong>c&ugrave; lao Long Trị</strong></em></a>, khu du lịch cồn H&ocirc; v&agrave;o bất kỳ khoảng thời gian n&agrave;o trong năm cũng được. Tuy nhi&ecirc;n, lời khuy&ecirc;n m&agrave; người bản địa thường d&agrave;nh cho du kh&aacute;ch l&agrave; n&ecirc;n lựa chọn đi v&agrave;o m&ugrave;a mưa v&igrave; đ&acirc;y l&agrave; khoảng thời gian c&acirc;y cối ph&aacute;t triển, kh&iacute; hậu dễ chịu, c&aacute;c lo&agrave;i động vật cũng đa dạng hơn m&ugrave;a kh&ocirc;. Cụ thể l&agrave; từ th&aacute;ng 5 đến th&aacute;ng 11 h&agrave;ng năm, với lượng mưa lớn c&ugrave;ng m&ugrave;a nước nổi k&eacute;o về th&igrave; thảm thực vật ở Rừng Đước ph&aacute;t triển mạnh mẽ thu h&uacute;t rất nhiều lo&agrave;i động vật về đ&acirc;y sinh sống, kiếm ăn v&agrave; sinh sản.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Khu du lịch Rừng Đước Trà Vinh - Cảnh đẹp hút khách\" src=\"https://luhanhvietnam.com.vn/du-lich/vnt_upload/news/08_2023/canh_dep_hut_khach.jpg\" style=\"height:800px; width:1202px\" title=\"Khu du lịch Rừng Đước ở Trà Vinh\" />Cảnh đẹp h&uacute;t kh&aacute;ch ở Tr&agrave; Vinh. Ảnh HoiAnTrip</span></span><br />\r\n&nbsp;</p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"10\" cellspacing=\"0\">\r\n	<thead>\r\n		<tr>\r\n			<td><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><strong>&gt;&gt;Xem th&ecirc;m:&nbsp;<a href=\"https://luhanhvietnam.com.vn/tour-du-lich/tour-mien-tay.html\" target=\"_blank\">Ch&ugrave;m tour Miền T&acirc;y gi&aacute; tốt&nbsp;</a></strong></em></span></span></td>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c hoạt động th&uacute; vị ở&nbsp;<strong>khu du lịch Rừng Đước Tr&agrave; Vinh</strong>?</span></span></h2>\r\n\r\n<h3><br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">1. Những trải nghiệm du lịch hấp dẫn</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khi check in Rừng Đước Tr&agrave; Vinh, du kh&aacute;ch tha hồ trải nghiệm vui chơi, tham quan, kh&aacute;m ph&aacute; m&agrave; nổi bật nhất l&agrave; c&aacute;c hoạt động sau:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Du ngoạn rừng Đước bằng đường bộ hoặc đường thủy: t&ugrave;y v&agrave;o sở th&iacute;ch v&agrave; lịch tr&igrave;nh m&agrave; bạn c&oacute; thể chọn một trong hai c&aacute;ch tham quan nhưng kh&aacute;m ph&aacute; s&ocirc;ng nước v&agrave; khu rừng bằng đường thủy thường được kh&aacute;ch du lịch lựa chọn nhiều hơn v&igrave; nếu đi theo c&aacute;ch n&agrave;y bạn sẽ được len lỏi v&agrave;o c&aacute;c bụi đước lớn, nhỏ, c&oacute; c&acirc;y đ&atilde; h&agrave;ng chục năm tuổi, mọc đan xen nhau ph&iacute;a dưới nhưng ph&iacute;a tr&ecirc;n vẫn chừa lại khoảng kh&ocirc;ng tạo th&agrave;nh một khoảng trời xanh ng&aacute;t. Du kh&aacute;ch tha hồ tận hưởng cảm gi&aacute;c th&uacute; vị v&agrave; mới lạ khi từng chiếc xuồng m&aacute;y lướt bồng bềnh giữa kh&ocirc;ng gian trong l&agrave;nh của bạt ng&agrave;n c&acirc;y cối, chim ch&oacute;c v&agrave; nhiều lo&agrave;i sinh vật đặc trưng kh&aacute;c của rừng c&acirc;y - s&ocirc;ng nước.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Khu du lịch Rừng Đước Trà Vinh - Khung cảnh nên thơ\" src=\"https://luhanhvietnam.com.vn/du-lich/vnt_upload/news/08_2023/khung_canh_nen_tho.jpg\" style=\"height:800px; width:1202px\" title=\"Khu du lịch Rừng Đước ở Trà Vinh\" />Khung cảnh n&ecirc;n thơ kh&ocirc;ng thua g&igrave; Rừng tr&agrave;m Tr&agrave; Sư. Ảnh: top10travinh</span></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Rừng Đước Tr&agrave; Vinh cũng c&oacute; sự đa dạng c&aacute;c lo&agrave;i động, thực vật như rừng ngập mặn Cần Giờ hay&nbsp;<a href=\"https://luhanhvietnam.com.vn/du-lich/nam-can-ca-mau-co-gi-choi-vui-nhat.html\"><em><strong>Năm Căn C&agrave; Mau</strong></em></a>&nbsp;v&igrave; c&oacute; nhiều n&eacute;t tương đồng về kh&iacute; hậu, thổ nhưỡng. Hệ động vật nơi đ&acirc;y thật sự phong ph&uacute; với n&agrave;o l&agrave; kỳ đ&agrave;, s&oacute;c,... rồi chồn, rắn hổ mang, vọp,... c&ugrave;ng nhiều loại t&ocirc;m c&aacute;, thủy sản v&agrave; nhiều lo&agrave;i chim đặc trưng của rừng ngập mặn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đến với khu sinh th&aacute;i, bạn sẽ cảm thấy như đang sống chậm lại, trải nghiệm cảm gi&aacute;c h&ograve;a m&igrave;nh ho&agrave;n to&agrave;n v&agrave;o thi&ecirc;n nhi&ecirc;n, đất trời, tha hồ h&iacute;t thở bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh, m&aacute;t mẻ của miền qu&ecirc;.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Khu du lịch Rừng Đước Trà Vinh - Cảnh đẹp như tranh vẽ\" src=\"https://luhanhvietnam.com.vn/du-lich/vnt_upload/news/08_2023/canh_dep_nhu_tranh_ve.jpg\" style=\"height:800px; width:800px\" title=\"Khu du lịch Rừng Đước ở Trà Vinh\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ngo&agrave;i những hoạt động tr&ecirc;n th&igrave; khi đến với&nbsp;<em><strong>khu du lịch Rừng Đước Tr&agrave; Vinh</strong></em>, bạn c&ograve;n c&oacute; cơ hội nghe những c&acirc;u h&ograve; đậm chất s&ocirc;ng nước, thấm đẫm dư vị th&ocirc;n qu&ecirc; của người d&acirc;n địa phương. Khi ngồi tr&ecirc;n những chiếc xuồng nhỏ lướt đi, đừng qu&ecirc;n d&agrave;nh ch&uacute;t thời gian, tr&ograve; chuyện v&agrave; t&igrave;m hiểu về cuộc sống của người d&acirc;n địa phương nữa nh&eacute;. Cảm gi&aacute;c được nghe những giai điệu mộc mạc, những lời n&oacute;i ch&acirc;n qu&ecirc;, c&acirc;u chuyện sinh hoạt đời thường của người d&acirc;n v&ugrave;ng đất n&agrave;y giữa thi&ecirc;n nhi&ecirc;n m&aacute;t mẻ v&agrave; trong l&agrave;nh quả thật rất th&uacute; vị đấy.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">2. Thưởng thức c&aacute;c m&oacute;n ăn đặc sản</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&oacute; rất nhiều m&oacute;n ăn ngon, c&aacute;c đặc sản trứ danh đ&atilde; l&agrave;m n&ecirc;n t&ecirc;n tuổi của ẩm thực Tr&agrave; Vinh. V&igrave; vậy khi gh&eacute; thăm khu du lịch Rừng Đước n&oacute;i ri&ecirc;ng v&agrave; Tr&agrave; vinh n&oacute;i chung bạn đừng bỏ lỡ trải nghiệm ăn b&uacute;n nước l&egrave;o, b&uacute;n su&ocirc;ng, ch&aacute;o &aacute;m, b&aacute;nh canh Bến C&oacute;, c&aacute;c m&oacute;n l&agrave;m từ con ch&ugrave; ụ, c&aacute; nướng mọi tr&ecirc;n lửa đốt bằng đước kh&ocirc;, t&eacute;p bạc t&aacute;i chanh, c&aacute; n&acirc;u nấu lẩu chua với tr&aacute;i gi&aacute;c, nước mắm rươi,... C&aacute;c m&oacute;n ăn được b&aacute;n ở rất nhiều nơi nhưng du kh&aacute;ch h&atilde;y gh&eacute;&nbsp;<a href=\"https://luhanhvietnam.com.vn/du-lich/list-cac-quan-an-ngon-o-tra-vinh-khong-nen-bo-lo.html\"><em><strong>c&aacute;c qu&aacute;n ăn ngon ở Tr&agrave; Vinh</strong></em></a>&nbsp;để thoải m&aacute;i lựa chọn thưởng thức nh&eacute;.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Khu du lịch Rừng Đước Trà Vinh - Đặc sản xứ ngập mặn\" src=\"https://luhanhvietnam.com.vn/du-lich/vnt_upload/news/08_2023/dac_san_xu_ngap_man.jpg\" style=\"height:800px; width:1067px\" title=\"Khu du lịch Rừng Đước ở Trà Vinh\" />Đặc sản xứ ngập mặn. Ảnh VietFuntravel</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><strong>Một số qu&aacute;n ăn nổi tiếng m&agrave; du kh&aacute;ch c&oacute; thể tham khảo:</strong></em></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Qu&aacute;n 947</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Địa chỉ: Quốc lộ 53, kh&oacute;m 2, phường 2, thị x&atilde; Duy&ecirc;n Hải, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Giờ mở cửa: Từ 6h00 đến 23h00</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; chỉ từ: 15.000VNĐ đến 100.000VNĐ</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Qu&aacute;n ốc ngon</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Địa chỉ: QL53, Ấp Phước Trị, phường 1, thị trấn Duy&ecirc;n Hải, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Giờ mở cửa: 16h00 đến 23h00</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; chỉ từ: 20.000VNĐ đến 200.000VNĐ</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Khu du lịch Rừng Đước Trà Vinh - Nếm thử các đặc sản\" src=\"https://luhanhvietnam.com.vn/du-lich/vnt_upload/news/08_2023/nem_thu_cac_dac_san.jpg\" style=\"height:800px; width:1538px\" title=\"Khu du lịch Rừng Đước ở Trà Vinh\" />Nếm thử c&aacute;c đặc sản ở Tr&agrave; Vinh. Ảnh: vntrip</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Qu&aacute;n Ch&iacute; T&acirc;m</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Địa chỉ: X&atilde; Duy&ecirc;n Hải, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Giờ mở cửa: Từ 07h00 đến 21h00</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; chỉ từ: 50.000VNĐ đến 500.000VNĐ</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Nh&agrave; h&agrave;ng To&agrave;n Vinh</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Địa chỉ: Quốc lộ 53, kh&oacute;m 4, phường 1, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Giờ mở cửa: Cả ng&agrave;y</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; chỉ từ: 50.000đ &ndash; 2.500.000đ</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">3. Ở đ&acirc;u khi đi&nbsp;<strong>khu du lịch Rừng Đước Tr&agrave; Vinh</strong>?</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Sau khi vui chơi thỏa th&iacute;ch v&agrave; trải nghiệm nhiều hoạt động th&uacute; vị, thưởng thức c&aacute;c m&oacute;n ngon Tr&agrave; Vinh nức tiếng th&igrave; bạn c&oacute; thể chọn cho m&igrave;nh trong những nơi lưu tr&uacute; gần khu sinh th&aacute;i để nghỉ ngơi qua đ&ecirc;m:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Nh&agrave; nghỉ Hoa Phượng: kh&oacute;m 1, thị x&atilde; Duy&ecirc;n Hải, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Kh&aacute;ch sạn Hải Dương: số 14 đường 2 &ndash; 9, phường 1, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Kh&aacute;ch sạn &Uacute;t T&acirc;m: kh&oacute;m 3, phường 1, thị x&atilde; Duy&ecirc;n Hải, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Nh&agrave; nghỉ Th&agrave;nh Lộc: đường 19 &ndash; 5, thị x&atilde; Duy&ecirc;n Hải, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Nh&agrave; nghỉ &Uacute;t &Aacute;nh 2: ấp Thống Nhất, x&atilde; Long To&agrave;n, thị x&atilde; Duy&ecirc;n Hải, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Nh&agrave; nghỉ Thanh Linh: ấp Cồn Trứng, x&atilde; Trường Long H&ograve;a, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Kh&aacute;ch sạn To&agrave;n Vinh: khu 4, thị x&atilde; Duy&ecirc;n Hải, huyện Duy&ecirc;n Hải, tỉnh Tr&agrave; Vinh.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Khu du lịch Rừng Đước Trà Vinh 0 Khu rừng ngập mặn\" src=\"https://luhanhvietnam.com.vn/du-lich/vnt_upload/news/08_2023/khu_rung_ngap_man.jpg\" style=\"height:800px; width:1425px\" title=\"Khu du lịch Rừng Đước ở Trà Vinh\" /></span></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><strong>Khu du lịch Rừng Đước&nbsp;Tr&agrave; Vinh</strong></em>&nbsp;sở hữu nhiều điều kiện tự nhi&ecirc;n, thổ nhưỡng thuận luận lợi với hệ sinh th&aacute;i đa dạng v&agrave; kh&ocirc;ng kh&iacute; trong l&agrave;nh, phong cảnh c&ograve;n giữ được n&eacute;t hoang sơ với nhiều trải nghiệm th&uacute; vị. C&oacute; lẽ v&igrave; vậy m&agrave; nơi n&agrave;y được xếp v&agrave;o h&agrave;ng những cảnh quan tuyệt đẹp của v&ugrave;ng đất Tr&agrave; Vinh. Với những th&ocirc;ng tin, hy vọng bạn c&oacute; được cho m&igrave;nh h&agrave;nh trang để chuẩn bị kh&aacute;m ph&aacute; -&nbsp;<em><strong><a href=\"https://luhanhvietnam.com.vn/du-lich/tra-vinh.html\">du lịch&nbsp;Tr&agrave; Vinh</a></strong></em>, check in khu du lịch Rừng Đước thật dễ chịu v&agrave; thoải m&aacute;i nh&eacute;. Ch&uacute;c bạn c&oacute; chuyến đi chơi vui vẻ.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><iframe frameborder=\"0\" height=\"411\" id=\"258680046\" src=\"https://www.youtube.com/embed/2ODkhnC8I2w?enablejsapi=1&amp;origin=https%3A%2F%2Fwww.dienmayxanh.com\" title=\"Khu sinh thái Rừng Đước Duyên Hải - Trà Vinh\" width=\"730\"></iframe></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Ch&uacute;c c&aacute;c bạn c&oacute; những chuyến đi thật vui v&agrave; &yacute; nghĩa!</em></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h1>&nbsp;</h1>\r\n', ' nằm ở xã Long Khánh, huyện Duyên Hải, tỉnh Trà Vinh và cách biển Ba Động khoảng 7km.', '0000-00-00', 3, 'khu rừng này có nhiều loại cây như đước, mắm, chà là gai, vẹt....trong đó nhiều nhất là cây đước. Và hơn thế còn được thử qua những món ăn địa phương như bún suông, cháo ám, mắm cá rươi,....', ' cách rừng đước rộng khoảng hơn 200ha và còn nằm trong hệ thống rừng ngập mặn vẹn biển Tây Nam Bộ.', 15);

